INSERT INTO `pools` (`id`, `type`, `active`, `wasActive`, `message`) VALUES
(1, 'Normal', 0, 1, 'Heute fühlt sich alles irgendwie... normal an? Aber lasst euch nicht täuschen, auch im Alltäglichen können sich spannende Begegnungen verbergen!'),
(2, 'Fighting', 0, 1, 'Macht euch bereit für einen Tag voller Kampfgeist! Kreaturen, die Stärke und Disziplin verkörpern, sind heute besonders aktiv.'),
(3, 'Flying', 0, 1, 'Blickt heute öfter mal nach oben! Alles, was Flügel hat und die Lüfte erobert, ist heute besonders unterwegs.'),
(4, 'Poison', 0, 0, 'Seid heute besonders umsichtig! Substanzen und Kreaturen mit einer giftigen Note könnten euren Weg kreuzen.'),
(5, 'Ground', 0, 1, 'Spürt die Verbindung zur Erde! Wesen, die fest im Boden verankert sind, zeigen heute ihre Präsenz.'),
(6, 'Rock', 0, 0, 'Achtet auf felsenfeste Erscheinungen! Kreaturen, so alt und beständig wie die Berge selbst, könnten sich heute zeigen.'),
(7, 'Bug', 0, 0, 'Es summt und krabbelt! Die Welt der Insekten und kleinen Krabbler ist heute besonders lebendig.'),
(8, 'Ghost', 0, 0, 'Ein Hauch des Jenseits weht herüber... Seid bereit für schaurig-schöne Begegnungen mit geisterhaften Wesen.'),
(9, 'Steel', 0, 1, 'Glänzende Rüstungen und metallische Klänge! Kreaturen von stählerner Natur sind heute nicht zu übersehen.'),
(10, 'Fire', 0, 0, 'Es wird feurig! Haltet Ausschau nach allem, was heute Funken sprüht und eine glühende Aura hat!'),
(11, 'Water', 0, 1, 'Eine Welle der Möglichkeiten! Achtet heute auf alles, was aus den Tiefen auftaucht oder sich im Nass wohlfühlt.'),
(12, 'Grass', 1, 1, 'Die Natur zeigt heute ihre volle Pracht! Alles, was Wurzeln schlägt und Blätter treibt, könnte eine Überraschung bereithalten.'),
(13, 'Electric', 0, 1, 'Heute liegt Hochspannung in der Luft! Bereitet euch auf elektrisierende Begegnungen vor, die euch wachrütteln könnten.'),
(14, 'Psychic', 0, 1, 'Eine mystische Energie ist spürbar... Verlasst euch auf euren sechsten Sinn, denn Wesen mit mentaler Stärke sind heute nah.'),
(15, 'Ice', 0, 0, 'Ein frostiger Wind weht! Kreaturen, die die Kälte meistern, könnten heute für eiskalte Überraschungen sorgen.'),
(16, 'Dragon', 0, 1, 'Eine legendäre Aura liegt in der Luft! Mythische und ehrfurchtgebietende Drachenwesen könnten heute gesichtet werden.'),
(17, 'Dark', 0, 0, 'Wenn die Dämmerung hereinbricht, werden sie aktiv... Haltet die Augen offen für die scheuen und mysteriösen Unlicht-Kreaturen.'),
(18, 'Fairy', 0, 1, 'Ein Hauch von Magie und Zauber! Anmutige und verspielte Feenwesen könnten heute eure Herzen erobern.');
